import React, { Component } from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik'
import ClientDataService from '/Users/kaan/Desktop/virgosolAppReact/virgosol-app-react/src/api/virgosolApp/ClientDataService.js'

class ClientComponent extends Component{
    constructor(props){
        super(props)
        this.state={
            id:this.props.match.params.id,
            name: "asd11",
            contact:"123123",
            address:"asdasd",
            numberOfEmployees: 43
        }
        this.onSubmit = this.onSubmit.bind(this)
        this.validate = this.validate.bind(this)

    }

    componentDidMount(){
        let userName = AuthenticationService.getLoggedInUserName
        ClientDataService.retrieveClient(userName,id)
    }
    onSubmit(values){

    }

    validate(values){
        let errors = {}
        if(!values.name){
            errors.name="Please enter a client name"
        }

        if(typeof(values.numberOfEmployees===String)){
            errors.numberOfEmployees="Please enter a number of employees"
        }

        if(typeof(values.contact===String)){
            errors.numberOfEmployees="Please enter a valid contact number"
        }
        return errors
    }
    render(){
        let {name,contact,address,numberOfEmployees} = this.state

        return(
            <div>
                <h1>Client</h1>
                <div className = "container">
                    <Formik
                        initialValues = {{name,contact,address,numberOfEmployees}}
                        onSubmit = {this.onSubmit}
                        validateOnBlur={false}
                        validateOnChange={false}
                        validate = {this.validate}
                    >
                        {
                            (props) => (
                                <Form>
                                    <ErrorMessage name ="numberOfEmployees" component = "div" className = "alert alert-warning"/>
                                    <ErrorMessage name ="name" component = "div" className = "alert alert-warning"/>
                                    <ErrorMessage name ="contact" component = "div" className = "alert alert-warning"/>
                                    <fieldset className = "form-group">
                                        <label>Name</label>
                                        <Field className = "form-control" type = "text" name = "name"/>
                                    </fieldset>
                                    <fieldset className = "form-group">
                                        <label>Contact</label>
                                        <Field className = "form-control" type = "text" name = "contact"/>
                                    </fieldset>
                                    <fieldset className = "form-group">
                                        <label>Address</label>
                                        <Field className = "form-control" type = "text" name = "address"/>
                                    </fieldset>
                                    <fieldset className = "form-group">
                                        <label>Number of Employees</label>
                                        <Field className = "form-control" type = "text" name = "numberOfEmployees"/>
                                    </fieldset>
                                    <button className = "btn btn-success" type = "submit">Save</button>
                                </Form>
                            )

                        }

                    </Formik>
                </div>
            </div>
        )
    }
}

export default ClientComponent
